        if(event.target.id != 'search_open' && event.target.id != 'search_box' && event.target.id != 'search_input')
            $('#search_box').hide();
    });

    document.getElementById("toggleDropdown").addEventListener("click", toggleDropdownMenu);
    document.getElementById("search_open").addEventListener("click", openSearch);
    document.getElementById("search_close").addEventListener("click", closeSearch);
    document.getElementById("basket_icon").addEventListener("click", toggleBasketContent)

    function toggleDropdownMenu() {
        let toggleDropdown = document.getElementById("dropdown-menu")
        if (toggleDropdown.style.display === "block") {
            toggleDropdown.style.display = "none"
        } else {
            toggleDropdown.style.display = "block"
        }
        
    }

    function openSearch() {
        let openSearch = document.getElementById("search_box")
        openSearch.style.display = "flex"
    }

    function closeSearch() {
        let openSearch = document.getElementById("search_box")
        openSearch.style.display = "none"
    }

    function toggleBasketContent() {
        let toggleDropdown = document.getElementById("basket_content")
        if (toggleDropdown.style.display === "flex") {
            toggleDropdown.style.display = "none"
        } else {
            toggleDropdown.style.display = "flex"
        }
        
    }
</script>